body {
    margin: 0;
    font-family: Tahoma, sans-serif;
    background: #f9f9ef;
}

/* Sidebar */
.sidebar {
    width: 220px;
    height: 100vh;
    background: #b89172;
    float: left;
    color: #fff;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding-top: 30px;
}
.sidebar a {
    color: #fff;
    text-decoration: none;
    margin: 15px 0;
    display: block;
    width: 100%;
    text-align: center;
    transition: background 0.3s;
}
.sidebar a:hover {
    background: rgba(255,255,255,0.2);
    border-radius: 6px;
}

/* Content */
.content {
    margin-left: 220px;
    padding: 20px;
}

/* Topbar */
.topbar {
    background: #d4b295;
    padding: 15px 0;
    text-align: center;
    margin-bottom: 25px;
    box-sizing: border-box;
    border-radius: 6px 6px 0 0;
    box-shadow: 0 2px 5px rgba(0,0,0,0.1);
}
.topbar img {
    max-height: 60px;
    display: block;
    margin: 0 auto;
}

/* Headline */
h2 {
    margin: 20px 0;
    font-weight: 600;
    color: #4d3a2c;
}

/* Table */
table {
    width: 100%;
    border-collapse: collapse;
    border-radius: 6px;
    overflow: hidden;
    box-shadow: 0 2px 5px rgba(0,0,0,0.05);
}
th, td {
    border: 1px solid #ccc;
    padding: 10px;
    text-align: center;
    transition: background 0.3s;
}
th {
    background: #e5c3a6;
    font-weight: 600;
}
tr:nth-child(even) {
    background: #fdf5ef;
}
tr:hover {
    background: #ffe8d0;
}

/* Search box */
.search-box {
    margin: 10px 0 15px;
}
input, select {
    padding: 8px;
    border: 1px solid #ccc;
    border-radius: 4px;
    outline: none;
}
input:focus, select:focus {
    border-color: #b89172;
}

/* PDF button */
.btn-pdf {
    margin-top: 20px;
    background: #f77c7c;
    color: #000;
    padding: 10px 25px;
    border: none;
    border-radius: 6px;
    cursor: pointer;
    font-weight: bold;
    transition: background 0.3s;
}
.btn-pdf:hover {
    background: #f55c5c;
}
