+    padding: 10px 25px;
+    border: none;
+    border-radius: 6px;
+    cursor: pointer;
+    font-weight: bold;
+    transition: background 0.3s;
+}
+.btn-upload:hover {
+    background: #3ad334ff;
+}
+
+.btn-edit { background: #4caf50; color: #fff; }
+.btn-save { background: #2196f3; color: #fff; }
+.btn-cancel { background: #777; color: #fff; }
