 });
 
+
+// ปุ่มเปอร์เซ็นต์
+document.querySelector(".add-percent").addEventListener("click", function() {
+    let percent = prompt("กรอกเปอร์เซ็นต์ที่จะเพิ่ม (เช่น 10 สำหรับ +10%)");
+    percent = parseFloat(percent);
+    if(isNaN(percent)) return alert("กรุณากรอกตัวเลขที่ถูกต้อง");
+
+    let table = document.getElementById("itemsTable").querySelector("tbody");
+
+    // เพิ่มแถวใหม่สำหรับเปอร์เซ็นต์
+    let rowCount = table.rows.length + 1;
+    let newRow = table.insertRow();
+    newRow.innerHTML = `
+        <td>${rowCount}</td>
+        <td><input type="text" name="item_name[]" value=" + ${percent}%"></td>
+        <td><input type="number" name="qty[]" class="qty"></td>
+        <td><input type="number" name="price[]" class="price"></td>
+        <td><input type="text" name="unit[]"></td>
+        <td><input type="text" name="total[]" class="total" readonly></td>
+        <td><button type="button" class="remove-btn">-</button></td>
+    `;
+    attachEvents(newRow);
+    updateGrandTotal();
+});
+
+// ปรับ attachEvents ให้รองรับ qty/price ของแถว +%
+function attachEvents(row) {
+    let removeBtn = row.querySelector(".remove-btn");
+    if(removeBtn){
+        removeBtn.addEventListener("click", function() {
+            row.remove();
+            updateRowNumbers();
+            updateGrandTotal();
+        });
+    }
+
+    let qtyInput = row.querySelector(".qty");
+    let priceInput = row.querySelector(".price");
+    let totalInput = row.querySelector(".total");
+
+    if(qtyInput && priceInput && totalInput){
+        function recalc(){
+            let qty = parseFloat(qtyInput.value) || 0;
+            let price = parseFloat(priceInput.value) || 0;
+            totalInput.value = (qty * price).toFixed(2);
+            updateGrandTotal();
+        }
+        qtyInput.addEventListener("input", recalc);
+        priceInput.addEventListener("input", recalc);
+    }
+}
+
+function updateRowNumbers(){
+    let table = document.getElementById("itemsTable").querySelector("tbody");
+    Array.from(table.rows).forEach((row,index)=>{
+        row.cells[0].textContent = index + 1;
+    });
+}
+
